// PostRectanglejobs - Create RectangleJob
func DoPostRectanglejobs(c *gin.Context) {

	var rJob Rectanglejob
	err := c.BindJSON(&rJob)
	if err != nil {
		c.JSON(http.StatusBadRequest, err)
		return
	}

	insertJobResult, err := dbo.InsertJob(rJob)
	if err != nil {
		c.JSON(http.StatusInternalServerError, err)
		return
	}

	rJobGeomotry, err := json.Marshal(rJob.Geometry)
	if err != nil {
		c.JSON(http.StatusBadRequest, err)
		return
	}

	body := "{\"id\":\"" +
		insertJobResult +
		"\", \"data\":" +
		string(rJobGeomotry) +
		", \"nextstep\":null}"

	dbo.Enqueue(body)

	c.JSON(http.StatusOK, rJob)
}

